// Test what URLs are actually being generated by home.html
console.log('Testing video proxy URL generation...');

// Simulate home.html proxy configuration
const PROXY_HOST = 'localhost';
const PROXY_PORT = 8083;

function proxyUrl(originalUrl) {
    if (!originalUrl) return '';
    
    // Skip non-video URLs
    if (!originalUrl.includes('mp4') && !originalUrl.includes('video')) {
        return originalUrl;
    }
    
    // Create proxy URL that routes through port 8083
    const encodedUrl = encodeURIComponent(originalUrl);
    const proxyUrl = `http://${PROXY_HOST}:${PROXY_PORT}/proxy?url=${encodedUrl}`;
    
    console.log('[PROXY] Routing:', originalUrl, '→', proxyUrl);
    return proxyUrl;
}

// Test with actual video URLs from home.html
const testUrls = [
    'https://commondatastorage.googleapis.com/gtv-videos-bucket/sample/BigBuckBunny.mp4',
    'https://vjs.zencdn.net/v/oceans.mp4',
    'https://media.w3.org/2010/05/sintel/trailer_hd.mp4'
];

console.log('\nTesting proxy URL generation:');
testUrls.forEach(url => {
    const result = proxyUrl(url);
    console.log(`Original: ${url}`);
    console.log(`Proxied: ${result}`);
    console.log('---');
});

console.log('\n✅ URL generation test completed');
